import FloatingLabel from "react-bootstrap/FloatingLabel";
import Form from "react-bootstrap/Form";
import Button from 'react-bootstrap/Button';
import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';
import AOS from 'aos';
import 'aos/dist/aos.css';



const ContactUs = () => {
  const form = useRef();
  AOS.init();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_ot7levi', 'template_3ivi87e', e.target, 'DA0wQet_Akzp42uGA')
        .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <div id="contact" style={{ margin: "50px 100px 30px 100px"}}>
      <h1 style={{color: "white", fontFamily: "fantasy"}}>Contactez-Moi</h1>
    <Form ref={form} onSubmit={sendEmail}>
    <div data-aos="zoom-in-right">  
      <FloatingLabel
        controlId="floatingInput"
        label="Name"
        className="mb-3"
      >
        <Form.Control type="text" placeholder="your_name" name="user_name"/>
      </FloatingLabel>
        </div>

        <div data-aos="zoom-in-right">
      <FloatingLabel
        controlId="floatingInput"
        label="Email address"
        className="mb-3"
      >
        <Form.Control type="email" placeholder="name@example.com" name="user_email" />
      </FloatingLabel>
      </div>

      <div data-aos="zoom-in-right">
      <FloatingLabel controlId="floatingTextarea2" label="Un commentaire ?">
        <Form.Control
          as="textarea"
          placeholder="Leave a comment here"
          style={{ height: "100px" }}
          name="message"
        />
      </FloatingLabel>
      {/* </div>
      <div data-aos="zoom-in-right" style={{marginBottom: "100px"}}> */}
      <Button variant="primary" type="submit" style={{margin: "10px"}}>
        Submit
      </Button>
      </div>
    </Form>
    </div>
  );
};

export default ContactUs;
